import React, { useEffect, useState } from "react";
import images from './img.js';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TouchUIDemo from './data.js';
import { Form, Button, Toast } from "react-bootstrap";
import './choise.css';

function Choise() {
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState('');
  const [overallRating, setOverallRating] = useState(0);
  const [courtConditionRating, setCourtConditionRating] = useState(0);
  const [ringsConditionRating, setRingsConditionRating] = useState(0);
  const [comment, setComment] = useState('');
  const path_to_avatar = "http://example.com/path/to/avatars";

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await axios.get('');
        if (Array.isArray(response.data)) {
          setReviews(response.data);
        } else {
          console.error('Fetched data is not an array:', response.data);
          setReviews([]);
        }
      } catch (error) {
        console.error('Error fetching reviews:', error);
        setReviews([]);
      }
    };
    fetchReviews();
  }, []);

  const submitReview = async (e) => {
    e.preventDefault();
    if (!name || !comment || overallRating === 0 || courtConditionRating === 0 || ringsConditionRating === 0) {
      alert("Пожалуйста, заполните все поля!");
      return;
    }
    const newReview = {
      name,
      overallRating,
      courtConditionRating,
      ringsConditionRating,
      comment
    };
    try {
      await axios.post('', newReview);
      setReviews([...reviews, newReview]);
      setName('');
      setOverallRating(0);
      setCourtConditionRating(0);
      setRingsConditionRating(0);
      setComment('');
      toast.success("Отзыв успешно отправлен!");
    } catch (error) {
      console.error('Error submitting review:', error);
      toast.error("Ошибка при отправке отзыва.");
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <img
            src="https://открыватор.москва/file/2015/10/karta-moskvi.jpg"
            height="180px"
            width="250px"
          />
        </div>
        <div className="col">
          <h5 className="card-title">Набережная</h5>
          <p className="card-text">
            <img src={images.point} height="23px" width="23px" /> Адрес:
          </p>
          <Button label="Я приду" severity="success" text />
        </div>
      </div>
      <br />
      <div className="row">{TouchUIDemo()}</div>
      <div className="review-container">
        <h2>Отзывы</h2>
        <div>
          {Array.isArray(reviews) && reviews.length > 0 ? (
            reviews.map((review, index) => (
              <Toast key={index}>
                <Toast.Header>
                  <strong className="mr-auto">{review.name}</strong>
                  <img
                    src={`${path_to_avatar}/${review.name}`}
                    alt="avatar"
                    className="avatar"
                  />
                </Toast.Header>
                <Toast.Body>
                  <div className="content">
                    <p>Общее впечатление: {[...Array(5)].map((_, i) => (
                      <span key={i} className={review.overallRating > i ? 'filled-star' : 'empty-star'}>★</span>
                    ))}</p>
                    <p>Состояние корта: {[...Array(5)].map((_, i) => (
                      <span key={i} className={review.courtConditionRating > i ? 'filled-star' : 'empty-star'}>★</span>
                    ))}</p>
                    <p>Состояние колец: {[...Array(5)].map((_, i) => (
                      <span key={i} className={review.ringsConditionRating > i ? 'filled-star' : 'empty-star'}>★</span>
                    ))}</p>
                    <p>{review.comment}</p>
                  </div>
                </Toast.Body>
              </Toast>
            ))
          ) : (
            <p>Нет отзывов для отображения.</p>
          )}
        </div>
        <Form onSubmit={submitReview}>
          <Form.Group controlId="formName">
            <Form.Label>Имя</Form.Label>
            <Form.Control
              type="text"
              placeholder="Введите ваше имя"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formOverallRating">
            <Form.Label>Общее впечатление</Form.Label>
            <div className="wrapper">
              {[...Array(5)].map((_, i) => (
                <React.Fragment key={i}>
                  <input
                    name="overallRating"
                    type="radio"
                    id={`overall-st${i + 1}`}
                    value={i + 1}
                    checked={overallRating === (i + 1)}
                    onChange={() => setOverallRating(i + 1)}
                  />
                  <label htmlFor={`overall-st${i + 1}`}>
                    <span className={overallRating >= (i + 1) ? 'filled-star' : 'empty-star'}>★</span>
                  </label>
                </React.Fragment>
              ))}
            </div>
          </Form.Group>
          <Form.Group controlId="formCourtConditionRating">
            <Form.Label>Состояние корта</Form.Label>
            <div className="wrapper">
              {[...Array(5)].map((_, i) => (
                <React.Fragment key={i}>
                  <input
                    name="courtConditionRating"
                    type="radio"
                    id={`court-st${i + 1}`}
                    value={i + 1}
                    checked={courtConditionRating === (i + 1)}
                    onChange={() => setCourtConditionRating(i + 1)}
                  />
                  <label htmlFor={`court-st${i + 1}`}>
                    <span className={courtConditionRating >= (i + 1) ? 'filled-star' : 'empty-star'}>★</span>
                  </label>
                </React.Fragment>
              ))}
            </div>
          </Form.Group>
          <Form.Group controlId="formRingsConditionRating">
            <Form.Label>Состояние колец</Form.Label>
            <div className="wrapper">
              {[...Array(5)].map((_, i) => (
                <React.Fragment key={i}>
                  <input
                    name="ringsConditionRating"
                    type="radio"
                    id={`rings-st${i + 1}`}
                    value={i + 1}
                    checked={ringsConditionRating === (i + 1)}
                    onChange={() => setRingsConditionRating(i + 1)}
                  />
                  <label htmlFor={`rings-st${i + 1}`}>
                    <span className={ringsConditionRating >= (i + 1) ? 'filled-star' : 'empty-star'}>★</span>
                  </label>
                </React.Fragment>
              ))}
            </div>
          </Form.Group>
          <Form.Group controlId="formComment">
            <Form.Label>Комментарий</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Введите ваш отзыв"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" type="submit">Отправить отзыв</Button>
        </Form>
      </div>
      <ToastContainer />
    </div>
  );
}

export default Choise;
