import React, { useState, useEffect } from "react";
import './App.css';
import images from './components/img.js';
import header from './components/header.js';
import Card from './components/card.js'; // Используйте заглавные буквы для компонентов
import Reg from './components/reg.js';
import menu from './components/menu.js'; // Импортируйте как переменную
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Login from './components/login.js';
import Choise from './components/choise.js'; // Изменен импорт, чтобы использовать заглавные буквы
import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";
import LoadingScreen from './components/LoadingScreen.js';

function Header() {
  return header();
}

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [showChoice, setShowChoice] = useState(false); // Состояние для отображения Choise

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 3000);
  }, []);

  const handleToggleChoice = () => {
    setShowChoice((prev) => !prev); // Переключаем состояние
  };

  return (
    <div className="App">
      <BrowserRouter>
        <div className="container">
          <Header />
          <Routes>
            <Route path="/" element={
              <>
                <main>
                  <div className="row">
                    <div className="col">
                      {menu()} {/* Вызов функции menu здесь */}
                      <Card onToggleChoice={handleToggleChoice} showChoice={showChoice} /> {/* Передаем пропсы */}
                    </div>
                    <div className="col main-info">
                      {showChoice && <Choise />} {/* Условный рендеринг Choise */}
                    </div>
                  </div>
                </main>
              </>
            } />
            <Route path="/Login" element={<Login />} />
            <Route path="/Reg" element={<Reg />} />
          </Routes>
        </div>
      </BrowserRouter>

      {isLoading && (
        <LoadingScreen />
      )}
    </div>
  );
}

export default App;
