import React, { useEffect, useState } from "react";
import images from './img.js';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Form, Button, Toast } from "react-bootstrap";
import './choise.css';

function Choise() {
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState('');
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const path_to_avatar = "http://example.com/path/to/avatars";

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/reviews'); // Убедитесь в правильности URL
        if (Array.isArray(response.data)) {
          setReviews(response.data);
        } else {
          console.error('Fetched data is not an array:', response.data);
          setReviews([]);
        }
      } catch (error) {
        console.error('Error fetching reviews:', error);
        setReviews([]);
      }
    };
    fetchReviews();
  }, []);

  const submitReview = async (e) => {
    e.preventDefault();
    if (!name || !comment || rating === 0) {
      alert("Пожалуйста, заполните все поля!");
      return;
    }
    const newReview = { name, rating, comment };
    try {
      await axios.post('http://localhost:3000/api/reviews', newReview); // Убедитесь подставить корректный URL
      setReviews([...reviews, newReview]);
      setName('');
      setRating(0);
      setComment('');
      toast.success("Отзыв успешно отправлен!");
    } catch (error) {
      console.error('Error submitting review:', error);
      toast.error("Ошибка при отправке отзыва.");
    }
  };

  return (
    <div className="container">
      <h2>Отзывы</h2>
      {Array.isArray(reviews) && reviews.length > 0 ? (
        reviews.map((review, index) => (
          <Toast key={index}>
            <Toast.Header>
              <strong>{review.name}</strong>
              <img src={`${path_to_avatar}/${review.name}`} alt="avatar" className="avatar" />
            </Toast.Header>
            <Toast.Body>
              <div className="content">
                {Array.from({ length: 5 }, (_, i) => (
                  <span key={i} className={review.rating > i ? 'filled-star' : 'empty-star'}>
                    ★
                  </span>
                ))}
                <p>{review.comment}</p>
              </div>
            </Toast.Body>
          </Toast>
        ))
      ) : (
        <p>Нет отзывов для отображения.</p>
      )}
      <Form onSubmit={submitReview}>
        <Form.Group controlId="formName">
          <Form.Label>Имя</Form.Label>
          <Form.Control type="text" placeholder="Введите ваше имя" value={name} onChange={(e) => setName(e.target.value)} />
        </Form.Group>
        <Form.Group controlId="formRating">
          <Form.Label>Оценка</Form.Label>
          <div className="content">
            <div className="wrapper">
              {Array.from({ length: 5 }, (_, i) => (
                <React.Fragment key={i}>
                  <input
                    name="ratingRadio"
                    type="radio"
                    id={`st${i + 1}`}
                    value={i + 1}
                    checked={rating === (i + 1)}
                    onChange={() => setRating(i + 1)}
                  />
                  <label htmlFor={`st${i + 1}`}>
                    <span className={rating >= (i + 1) ? 'filled-star' : 'empty-star'}>
                      ★
                    </span>
                  </label>
                </React.Fragment>
              ))}
            </div>
          </div>
        </Form.Group>
        <Form.Group controlId="formComment">
          <Form.Label>Комментарий</Form.Label>
          <Form.Control as="textarea" rows={3} placeholder="Введите ваш отзыв" value={comment} onChange={(e) => setComment(e.target.value)} />
        </Form.Group>
        <Button variant="primary" type="submit">Отправить отзыв</Button>
      </Form>
      <ToastContainer />
    </div>
  );
}

export default Choise;
