import React, { useEffect, useState } from "react";
import images from './img.js';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TouchUIDemo from './data.js';
import { Form, Button, Toast } from "react-bootstrap";
import './choise.css';
import Stars from './stars.jsx';

function Choise() {
  const [selectedRating, setSelectedRating] = useState(0);
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState('');
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const path_to_avatar = "http://example.com/path/to/avatars";

  useEffect(() => {
    const fetchReviews = async () => {
      const response = await axios.get('/api/reviews');
      setReviews(response.data);
    };
    fetchReviews();
  }, []);

  const submitReview = async (e) => {
    e.preventDefault();
    if (!name || !comment || rating === 0) {
      alert("Пожалуйста, заполните все поля!");
      return;
    }
    const newReview = { name, rating, comment };
    await axios.post('/api/reviews', newReview);
    setReviews([...reviews, newReview]);
    setName('');
    setRating(0);
    setComment('');
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <img
            src="https://открыватор.москва/file/2015/10/karta-moskvi.jpg"
            height="180px"
            width="250px"
          />
        </div>
        <div className="col">
          <h5 className="card-title">Набережная</h5>
          <p className="card-text">
            <img src={images.point} height="23px" width="23px" /> Адрес:
          </p>
          <Button label="Я приду" severity="success" text />
        </div>
      </div>
      <br />
      <div className="row">{TouchUIDemo()}</div>
      <div className="review-container">
        <h2>Отзывы</h2>
        <div>
          {reviews.map((review, index) => (
            <Toast key={index}>
              <Toast.Header>
                <strong className="mr-auto">{review.name}</strong>
                <img
                  src={`${path_to_avatar}/${review.name}`}
                  alt="avatar"
                  className="avatar"
                />
              </Toast.Header>
              <Toast.Body>
                <div className="content">
                  <div className="wrapper">
                    {[...Array(5)].map((_, i) => (
                      <input
                        key={i}
                        name="ratingRadio"
                        type="radio"
                        id={`st${i + 1}`}
                        value={i + 1}
                        checked={review.rating === i + 1}
                        disabled
                      />
                    ))}
                    {[...Array(5)].map((_, i) => (
                      <label key={i} htmlFor={`st${i + 1}`}></label>
                    ))}
                  </div>
                </div>
                <p>{review.comment}</p>
              </Toast.Body>
            </Toast>
          ))}
        </div>
        <Form onSubmit={submitReview}>
          <Form.Group controlId="formName">
            <Form.Label>Имя</Form.Label>
            <Form.Control
              type="text"
              placeholder="Введите ваше имя"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formRating">
            <Form.Label>Оценка</Form.Label>
            <div className="content">
              <div className="wrapper">
                {[...Array(5)].map((_, i) => (
                  <React.Fragment key={i}>
                    <input
                      name="ratingRadio"
                      type="radio"
                      id={`st${i + 1}`}
                      value={i + 1}
                      checked={rating === i + 1}
                      onChange={() => setRating(i + 1)}
                    />
                    <label htmlFor={`st${i + 1}`}></label>
                  </React.Fragment>
                ))}
              </div>
            </div>
          </Form.Group>
          <Form.Group controlId="formComment">
            <Form.Label>Комментарий</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Введите ваш отзыв"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" type="submit">Отправить отзыв</Button>
        </Form>
      </div>
    </div>
  );
}

export default Choise;
