import React, { useState } from "react";
import { Calendar } from 'primereact/calendar';
import './../App.css';
import { Button } from 'primereact/button';
import { ButtonGroup } from 'primereact/buttongroup';
import { Dialog } from 'primereact/dialog';

export default function TouchUIDemo() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [showDialog, setShowDialog] = useState(false);
    const [time, setTime] = useState(null);

  const handleSave = () => {
    console.log('Данные сохранены:', selectedDate, time);
    setShowDialog(false);
  };

  const handleDelete = () => {
    setSelectedDate(null);
    setTime(null);
    setShowDialog(false);
  };

  const handleClear = () => {
    setSelectedDate(null);
    setTime(null);
  };

  // Функция для обновления состояния при выборе даты
  const handleDateChange = (e) => {
    setSelectedDate(e.value);
    setShowDialog(true); // Показываем диалог при выборе даты
  };

  // Функция для обновления состояния при выборе времени
  const handleTimeChange = (e) => {
    setTime(e.value);
  };

  return (
    <div className="card flex flex-wrap gap-3 p-fluid" style={{ border : 'none'}}>
      <div className="flex-auto">
        <label htmlFor="calendar-24h" className="font-bold block mb-2">
          Выберете время когда будет удобно прийти
        </label>
        <Calendar
          value={selectedDate}
          onChange={handleDateChange}
          selectionMode="single"
          readOnlyInput
        />
        <Dialog
          visible={showDialog}
          header="Выберите время"
          modal
          style={{ width: '400px' }}
          onHide={() => setShowDialog(false)}
        >
          <Calendar
          value={time}
          onChange={(e) => setTime(e.value)}
          timeOnly />
          <ButtonGroup>
            <Button label="Сохранить" icon="pi pi-check" onClick={handleSave} />
            <Button label="Удалить" icon="pi pi-trash" onClick={handleDelete} />
          </ButtonGroup>
        </Dialog>

      </div>
      <div>
      <Button
        label="Очистить"
        icon="pi pi-times"
        style={{ width: '130px', backgroundColor: 'gray', borderRadius: '4px'}}
        onClick={handleClear}
      />
        </div>
    </div>
  );
}
