import React, { useState } from 'react';
import { Card, Row, Col } from 'react-bootstrap';
import images from './img.js';
import Choise from './choise.js'; // Импорт компонента Choise
import './CustomCard.css'; // Импорт CSS-стилей

function CustomCard() {
  const [showChoice, setShowChoice] = useState(false);

  const openCort = () => {
    setShowChoice(prev => !prev); // Переключаем видимость блока Choise
  };

  return (
    <>
      <Card className="w-90" onClick={openCort}>
        <Row>
          <Col md={4}>
            <Card.Img
              src="https://открыватор.москва/file/2015/10/karta-moskvi.jpg"
              height="180px"
              width="170px"
              alt="картинка"
            />
          </Col>
          <Col md={6}>
            <Card.Body>
              <Card.Title>Набережная</Card.Title>
              <Card.Text>
                <img src={images.point} height="23px" width="23px" alt="иконка"/> Адрес:
              </Card.Text>
              <Card.Text>
                <img src={images.comand} height="23px" width="23px" alt="иконка"/> Людей сегодня: 0
              </Card.Text>
            </Card.Body>
          </Col>
          <Col md={2}>
            <div className="rating">
              {[...Array(5)].map((_, index) => (
                <span key={index} className="star">
                  {'☆'}
                </span>
              ))}
            </div>
          </Col>
        </Row>
      </Card>

      {showChoice && <Choise />} {/* Условный рендеринг компонента Choise */}
    </>
  );
}

export default CustomCard;
