import React from "react";
import { Button, Typography, Input } from 'antd';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const { TextArea } = Input;

const Comment = ({ reviews, overallRating, setOverallRating, courtConditionRating, setCourtConditionRating, ringsConditionRating, setRingsConditionRating, comment, setComment }) => {
  const path_to_avatar = "http://example.com/path/to/avatars"; // Установлено значение переменной

  const renderStars = (rating) => {
    return [...Array(5)].map((_, i) => (
      <span key={i} className={rating > i ? 'filled-star' : 'empty-star'}>★</span>
    ));
  };

  return (
    <div className="review-container my-4">
      <Typography.Title level={4}>Отзывы</Typography.Title>
      <div>
        {Array.isArray(reviews) && reviews.length > 0 ? (
          reviews.map((review, index) => (
            <div key={index} className="mb-3">
              <Typography.Text>{review.name}</Typography.Text>
              <img src={`${path_to_avatar}/${review.name}`} alt="avatar" className="avatar rounded-circle" height="30" width="30" />
              <Typography.Paragraph>
                <p>Общее впечатление: {renderStars(review.overallRating)}</p>
                <p>Состояние корта: {renderStars(review.courtConditionRating)}</p>
                <p>Состояние колец: {renderStars(review.ringsConditionRating)}</p>
                <p>{review.comment}</p>
              </Typography.Paragraph>
            </div>
          ))
        ) : (
          <Typography.Paragraph>Нет отзывов для отображения.</Typography.Paragraph>
        )}
      </div>
      <TextArea
        rows={4}
        placeholder="Ваш отзыв"
        value={comment}
        onChange={(e) => setComment(e.target.value)}
      />
      <ToastContainer />
    </div>
  );
}

export default Comment;
