import React, { useState } from "react";
import { Calendar } from 'primereact/calendar';
import './../App.css';
import { Button } from 'primereact/button';
import { ButtonGroup } from 'primereact/buttongroup';
import { Dialog } from 'primereact/dialog';

export default function TouchUIDemo() {
  const [datetime24h, setDateTime24h] = useState(null);
  const [dates, setDates] = useState(null);
  const [showDialog, setShowDialog] = useState(false);

  const handleSave = () => {
    console.log('Данные сохранены:', dates, datetime24h);
    setShowDialog(false);
  };

  const handleDelete = () => {
    setDates(null);
    setDateTime24h(null);
    setShowDialog(false);
  };

  const handleClear = () => {
    if (dates && dates.length > 0) {
      setDates(dates.slice(0, -1));
    } else if (datetime24h) {
      setDateTime24h(null);
    }
  };

  // Функция для обновления состояния при изменении времени
  const handleTimeChange = (e) => {
    // Обновляем только время, оставляя выбранные даты
    if (dates && dates.length > 0) {
      const newDates = dates.map((date) => {
        const newDate = new Date(date);
        newDate.setHours(e.value.getHours());
        newDate.setMinutes(e.value.getMinutes());
        return newDate;
      });
      setDates(newDates);
    } else {
      setDateTime24h(e.value);
    }
  };

  return (
    <div className="card flex flex-wrap gap-3 p-fluid">
      <div className="flex-auto">
        <label htmlFor="calendar-24h" className="font-bold block mb-2">
          Выберете время когда будет удобно прийти
        </label>
        <Calendar
          value={dates}
          onChange={(e) => setDates(e.value)}
          value={datetime24h}
          onChange={handleTimeChange}
          showTime hourFormat="24"
          selectionMode="multiple"
          readOnlyInput
          onClick={(dates) => setShowDialog(true)}  
        />

        <Dialog
          visible={showDialog}
          header="Сохранить/Удалить"
          modal
          style={{ width: '400px' }}
          onHide={() => setShowDialog(false)}
        >
          <ButtonGroup>
            <Button label="Save" icon="pi pi-check" onClick={handleSave} />
            <Button label="Delete" icon="pi pi-trash" onClick={handleDelete} />
          </ButtonGroup>
        </Dialog>
        <Button label="Clear" icon="pi pi-times" onClick={handleClear} />
      </div>
    </div>
  );
}
