import React from 'react';
import { Card } from 'antd';
import images from './img.js';

const CortCard = (props) => {
  const { onClick = () => {} } = props; // Установить значение по умолчанию

  const handleClick = () => {
    onClick(); // Вызвать onClick, если он передан
  };

  return (
    <Card onClick={handleClick} style={{ width: '90%' }}>
      <img src="https://открыватор.москва/file/2015/10/karta-moskvi.jpg" height="180px" width="170px" alt="court" />
      <h5>Набережная</h5>
      <p><img src={images.point} height="23px" width="23px" alt="" /> Адрес:</p>
      <p><img src={images.comand} height="23px" width="23px" alt="" /> Людей сегодня: 0</p>
      <form className="rating">
        {[1, 2, 3, 4, 5].map((i) => (
          <label key={i}>
            <input type="radio" name="stars" value={i} />
            <span className="icon">★</span>
          </label>
        ))}
      </form>
    </Card>
  );
}

export default CortCard;
