import React from 'react';
import { Card, Row, Col, Form } from 'react-bootstrap';
import images from './img.js';
import './CustomCard.css'; // Импорт CSS-стилей

function openCort() {
  // Реализация функции
}

function CustomCard() {
  return (
    <Card className="w-90" onClick={() => openCort()}>
      <Row>
        <Col md={4}>
          <Card.Img
            src="https://открыватор.москва/file/2015/10/karta-moskvi.jpg"
            height="180px"
            width="170px"
            alt="картинка"
          />
        </Col>
        <Col md={6}>
          <Card.Body>
            <Card.Title>Набережная</Card.Title>
            <Card.Text>
              <img src={images.point} height="23px" width="23px" alt="иконка"/> Адрес:
            </Card.Text>
            <Card.Text>
              <img src={images.comand} height="23px" width="23px" alt="иконка"/> Людей сегодня: 0
            </Card.Text>
          </Card.Body>
        </Col>
        <Col md={2}>
          <Form className="rating">
            {[...Array(5)].map((_, index) => (
              <Form.Check
                key={index}
                type="radio"
                label={
                  <span className="star">
                    {'☆'}
                  </span>
                }
                name="stars"
                value={index + 1}
                inline
              />
            ))}
          </Form>
        </Col>
      </Row>
    </Card>
  );
}

export default CustomCard;
