import React, { useEffect } from "react";
import { Button, Container, Typography, TextField } from "@mui/material";
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './comment.css'; // Импорт стилей для звездочек

function Comment({ reviews, overallRating, setOverallRating, courtConditionRating, setCourtConditionRating, ringsConditionRating, setRingsConditionRating, comment, setComment, submitReview }) {
  const path_to_avatar = "http://example.com/path/to/avatars";

  // JavaScript код для анимации звезд
  useEffect(() => {
    const starRating = new StarRating("form");
  }, []);

  const renderStars = (rating, setRating) => (
    <form className="rating">
      <div className="rating__stars">
        {[1, 2, 3, 4, 5].map((value) => (
          <div key={value}>
            <input
              id={`rating-${value}`}
              className={`rating__input rating__input-${value}`}
              type="radio"
              name="rating"
              value={value}
              checked={rating === value}
              onChange={() => setRating(value)}
            />
            <label className="rating__label" htmlFor={`rating-${value}`}>
              <svg className="rating__star" width="32" height="32" viewBox="0 0 32 32" aria-hidden="true">
                <g transform="translate(16,16)">
                  <circle className="rating__star-ring" fill="none" stroke="#000" strokeWidth="16" r="8" />
                </g>
                <g stroke="#000" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <g transform="translate(16,16) rotate(180)">
                    <polygon className="rating__star-stroke" points="0,15 4.41,6.07 14.27,4.64 7.13,-2.32 8.82,-12.14 0,-7.5 -8.82,-12.14 -7.13,-2.32 -14.27,4.64 -4.41,6.07" fill="none" />
                    <polygon className="rating__star-fill" points="0,15 4.41,6.07 14.27,4.64 7.13,-2.32 8.82,-12.14 0,-7.5 -8.82,-12.14 -7.13,-2.32 -14.27,4.64 -4.41,6.07" fill="#000" />
                  </g>
                </g>
              </svg>
              <span className="rating__sr">{value} star</span>
            </label>
          </div>
        ))}
        <p className="rating__display" data-rating={rating} hidden>{['Terrible', 'Bad', 'OK', 'Good', 'Excellent'][rating - 1]}</p>
      </div>
    </form>
  );

  return (
    <Container className="review-container my-4">
      <Typography variant="h4" className="mb-4">Отзывы</Typography>
      <div>
        {Array.isArray(reviews) && reviews.length > 0 ? (
          reviews.map((review, index) => (
            <div key={index} className="mb-3">
              <Typography variant="subtitle1">{review.name}</Typography>
              <img src={`${path_to_avatar}/${review.name}`} alt="avatar" className="avatar rounded-circle" height="30" width="30" />
              <Typography variant="body2">
                <p>Общее впечатление: {renderStars(review.overallRating, () => {})}</p>
                <p>Состояние корта: {renderStars(review.courtConditionRating, () => {})}</p>
                <p>Состояние колец: {renderStars(review.ringsConditionRating, () => {})}</p>
                <p>{review.comment}</p>
              </Typography>
            </div>
          ))
        ) : (
          <Typography variant="body1">Нет отзывов для отображения.</Typography>
        )}
      </div>
      <Typography variant="h6">Оцените:</Typography>
      <div>
        <p>Общее впечатление:</p>
        {renderStars(overallRating, setOverallRating)}
        <p>Состояние корта:</p>
        {renderStars(courtConditionRating, setCourtConditionRating)}
        <p>Состояние колец:</p>
        {renderStars(ringsConditionRating, setRingsConditionRating)}
      </div>
      <TextField
        label="Ваш отзыв"
        multiline
        rows={4}
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        fullWidth
      />
      <Button variant="contained" type="submit" onClick={submitReview}>Отправить отзыв</Button>
      <ToastContainer />
    </Container>
  );
}

// Класс для управления оценками
class StarRating {
  constructor(qs) {
    this.ratings = [
      { id: 1, name: "Terrible" },
      { id: 2, name: "Bad" },
      { id: 3, name: "OK" },
      { id: 4, name: "Good" },
      { id: 5, name: "Excellent" },
    ];
    this.rating = null;
    this.el = document.querySelector(qs);

    this.init();
  }

  init() {
    this.el?.addEventListener("change", this.updateRating.bind(this));

    // stop Firefox from preserving form data between refreshes
    try {
      this.el?.reset();
    } catch (err) {
      console.error("Element isn’t a form.");
    }
  }

  updateRating(e) {
    // clear animation delays
    Array.from(this.el.querySelectorAll(`[for*="rating"]`)).forEach((el) => {
      el.className = "rating__label";
    });

    const ratingObject = this.ratings.find((r) => r.id === +e.target.value);
    const prevRatingID = this.rating?.id || 0;

    let delay = 0;
    this.rating = ratingObject;
    this.ratings.forEach((rating) => {
      const { id } = rating;

      // add the delays
      const ratingLabel = this.el.querySelector(`[for="rating-${id}"]`);

      if (id > prevRatingID + 1 && id <= this.rating.id) {
        ++delay;
        ratingLabel.classList.add(`rating__label--delay${delay}`);
      }

      // hide ratings to not read, show the one to read
      const ratingTextEl = this.el.querySelector(`[data-rating="${id}"]`);

      if (this.rating.id !== id) {
        ratingTextEl.setAttribute("hidden", true);
      } else {
        ratingTextEl.removeAttribute("hidden");
      }
    });
  }
}

export default Comment;
