import React, { useState } from "react";
import { Calendar } from 'primereact/calendar';
import './../App.css';
import { Button } from 'primereact/button';
import { ButtonGroup } from 'primereact/buttongroup';

export default function TouchUIDemo() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [showCalendar, setShowCalendar] = useState(false);
  const [time, setTime] = useState(null);

  const handleSave = () => {
    console.log('Данные сохранены:', selectedDate, time);
    setShowCalendar(false);
  };

  const handleDelete = () => {
    setSelectedDate(null);
    setTime(null);
    setShowCalendar(false);
  };

  const handleClear = () => {
    setSelectedDate(null);
    setTime(null);
  };

  const handleDateChange = (e) => {
    setSelectedDate(e.value);
  };

  const handleTimeChange = (e) => {
    setTime(e.value);
  };

  return (
    <div className="card flex flex-wrap gap-3 p-fluid" style={{ border : 'none'}}>
      <label htmlFor="calendar-24h" className="font-bold block mb-2">
        Выберете время когда будет удобно прийти
      </label>
      <div class="row">
        <div class="col">
          <div className="flex-auto" >
            {showCalendar && (
              <div>
                <Calendar
                  value={selectedDate ? selectedDate : time} // Используем selectedDate если он не null, иначе time
                  onChange={handleTimeChange}
                  timeOnly
                />
                <ButtonGroup>
                  <Button label="Сохранить" icon="pi pi-check" onClick={handleSave} />
                  <Button label="Удалить" icon="pi pi-trash" onClick={handleDelete} />
                </ButtonGroup>
              </div>
            )}
            {!showCalendar && (
              <Calendar
                value={selectedDate}
                onChange={handleDateChange}
                showTime
                selectionMode="single"
                readOnlyInput
                onClick={() => setShowCalendar(true)}
              />
            )}
          </div>
        </div>
        <div class="col">
          <Button
            label="Очистить"
            icon="pi pi-times"
            style={{ width: '130px', backgroundColor: 'gray', borderRadius: '4px'}}
            onClick={handleClear}
          />
        </div>
      </div>
    </div>
  );
}
