import React, { useEffect, useState } from "react";
import images from './img.js';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TouchUIDemo from './data.js';
import { Form, Button } from "react-bootstrap";
import './choise.css';
import Comment from './comment.js'; // Имя изменено с маленькой на большую

function Choise() {
  const [reviews, setReviews] = useState([]);
  const [overallRating, setOverallRating] = useState(0);
  const [courtConditionRating, setCourtConditionRating] = useState(0);
  const [ringsConditionRating, setRingsConditionRating] = useState(0);
  const [comment, setComment] = useState('');
  const path_to_avatar = "http://example.com/path/to/avatars";

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await axios.get('');
        if (Array.isArray(response.data)) {
          setReviews(response.data);
        } else {
          console.error('Fetched data is not an array:', response.data);
          setReviews([]);
        }
      } catch (error) {
        console.error('Error fetching reviews:', error);
        setReviews([]);
      }
    };
    fetchReviews();
  }, []);

  const submitReview = async (e) => {
    e.preventDefault();
    if (!comment || overallRating === 0 || courtConditionRating === 0 || ringsConditionRating === 0) {
      alert("Пожалуйста, заполните все поля!");
      return;
    }

    const newReview = {
      name: "Имя пользователя",
      overallRating,
      courtConditionRating,
      ringsConditionRating,
      comment
    };

    try {
      await axios.post('', newReview);
      setReviews([...reviews, newReview]);
      setOverallRating(0);
      setCourtConditionRating(0);
      setRingsConditionRating(0);
      setComment('');
      toast.success("Отзыв успешно отправлен!");
    } catch (error) {
      console.error('Error submitting review:', error);
      toast.error("Ошибка при отправке отзыва.");
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <img
            src="https://открыватор.москва/file/2015/10/karta-moskvi.jpg"
            height="180px"
            width="250px"
          />
        </div>
        <div className="col">
          <h5 className="card-title">Набережная</h5>
          <p className="card-text">
            <img src={images.point} height="23px" width="23px" /> Адрес:
          </p>
          <Button label="Я приду" severity="success" text />
        </div>
      </div>
      <br />
      <Comment
        reviews={reviews}
        overallRating={overallRating}
        setOverallRating={setOverallRating}
        courtConditionRating={courtConditionRating}
        setCourtConditionRating={setCourtConditionRating}
        ringsConditionRating={ringsConditionRating}
        setRingsConditionRating={setRingsConditionRating}
        comment={comment}
        setComment={setComment}
        submitReview={submitReview}
      />
      <ToastContainer />
    </div>
  );
}

export default Choise;
