import React from "react";
import { TextField, Button, Typography } from "@mui/material";
import './comment.scss'; // Импорт стилей для оценивания

function Comment({ reviews, comment, setComment, submitReview, overallRating, setOverallRating }) {
  return (
    <div>
      <Typography variant="h6">Оставьте ваш отзыв:</Typography>
      <div className="star-cb-group">
        {[5, 4, 3, 2, 1].map((value) => (
          <span key={value}>
            <input
              type="radio"
              id={`rating-${value}`}
              name="rating"
              value={value}
              onChange={() => setOverallRating(value)}
              required
            />
            <label htmlFor={`rating-${value}`}>{value}</label>
          </span>
        ))}
        <input type="radio" id="rating-0" name="rating" value="0" className="star-cb-clear" />
        <label htmlFor="rating-0">0</label>
      </div>
      <TextField
        label="Ваш отзыв"
        multiline
        rows={4}
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        fullWidth
      />
      <Button variant="contained" type="submit" onClick={submitReview}>Отправить отзыв</Button>
    </div>
  );
}

export default Comment;
