import React, { useState } from "react";
import { Calendar } from 'primereact/calendar';
import './../App.css';
import { Button } from 'primereact/button';
import { ButtonGroup } from 'primereact/buttongroup';
import { Dialog } from 'primereact/dialog'; // Импортируем Dialog

export default function TouchUIDemo() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [time, setTime] = useState(null);
  const [showDialog, setShowDialog] = useState(false); // Состояние для диалога

  const handleSave = () => {
    console.log('Данные сохранены:', selectedDate, time);
    setSelectedDate(null); // Очищаем дату после сохранения
    setTime(null); // Очищаем время после сохранения
    setShowDialog(false); // Закрываем диалог
  };

  const handleDelete = () => {
    setSelectedDate(null);
    setTime(null);
  };

  const handleClear = () => {
    setSelectedDate(null);
    setTime(null);
  };

  // Функция для обновления состояния при выборе даты и времени
  const handleDateChange = (e) => {
    setSelectedDate(e.value);
    setTime(e.value);
  };

  // Функция для открытия диалога
  const openDialog = () => {
    setShowDialog(true);
  };

  return (
    <div className="card flex flex-wrap gap-3 p-fluid" style={{ border : 'none'}}>
      <label htmlFor="calendar-24h" className="font-bold block mb-2">
        Выберете время когда будет удобно прийти
      </label>
      <div class="row">
        <div class="col">
          <div className="flex-auto" >
            <Calendar
              value={selectedDate}
              onChange={handleDateChange}
              showTime
              selectionMode="single"
              readOnlyInput
              // Убираем footer
            />
          </div>
        </div>
        <div class="col">
          <Button
            label="Очистить"
            icon="pi pi-times"
            style={{ width: '130px', backgroundColor: 'gray', borderRadius: '4px'}}
            onClick={handleClear}
          />
        </div>
      </div>

    \
      <Dialog
        header="Подтверждение"
        visible={showDialog}
        onHide={() => setShowDialog(false)}
        style={{ width: '300px' }}
      >
        <p>Вы уверены, что хотите сохранить выбранные данные?</p>
        <ButtonGroup>
          <Button label="Отмена" icon="pi pi-times" onClick={() => setShowDialog(false)} />
          <Button label="Сохранить" icon="pi pi-check" onClick={handleSave} />
        </ButtonGroup>
      </Dialog>
    </div>
  );
}
