import React, { useState } from "react";
import { Calendar } from 'primereact/calendar';
import './../App.css';
import { Button } from 'primereact/button';
import { ButtonGroup } from 'primereact/buttongroup';
import { Dialog } from 'primereact/dialog';

export default function TouchUIDemo() {
  const [selectedDateTime, setSelectedDateTime] = useState(null); // Объединяем дату и время в одно состояние
  const [showDialog, setShowDialog] = useState(false);

  const handleSave = () => {
    console.log('Данные сохранены:', selectedDateTime);
    setShowDialog(false);
  };

  const handleDelete = () => {
    setSelectedDateTime(null);
    setShowDialog(false);
  };

  const handleClear = () => {
    setSelectedDateTime(null);
  };

  // Функция для обновления состояния при выборе даты и времени
  const handleDateTimeChange = (e) => {
    setSelectedDateTime(e.value); // Сохраняем выбранную дату и время в одно состояние
    setShowDialog(true); // Показываем диалог при выборе даты
  };

  return (
    <div className="card flex flex-wrap gap-3 p-fluid" style={{ border : 'none'}}>
      <div className="flex-auto">
        <label htmlFor="calendar-24h" className="font-bold block mb-2">
          Выберете время когда будет удобно прийти
        </label>
        <Calendar
          value={selectedDateTime}
          onChange={handleDateTimeChange}
          selectionMode="single"
          readOnlyInput
          showTime
        />
        <Dialog
          visible={showDialog}
          header="Выберите время"
          modal
          style={{ width: '400px' }}
          onHide={() => setShowDialog(false)}
        >
          
          <ButtonGroup>
            <Button label="Сохранить" icon="pi pi-check" onClick={handleSave} />
            <Button label="Удалить" icon="pi pi-trash" onClick={handleDelete} />
          </ButtonGroup>
        </Dialog>
        <Button
          label="Очистить"
          icon="pi pi-times"
          style={{ width: '130px', backgroundColor: 'gray', borderRadius: '4px'}}
          onClick={handleClear}
        />
      </div>
    </div>
  );
}
