import React from "react";
import { Form, Button, Toast, Container, Row, Col } from "react-bootstrap";
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TouchUIDemo from './data.js';
import './comment.css'; // Предполагается, что файл CSS существует для стилей

function Comment({ reviews, overallRating, setOverallRating, courtConditionRating, setCourtConditionRating, ringsConditionRating, setRingsConditionRating, comment, setComment, submitReview }) {
  const path_to_avatar = "http://example.com/path/to/avatars";

  return (
    <Container className="review-container my-4">
      <h2 className="mb-4">Отзывы</h2>
      <div>
        {Array.isArray(reviews) && reviews.length > 0 ? (
          reviews.map((review, index) => (
            <Toast key={index} className="mb-3">
              <Toast.Header>
                <strong className="mr-auto">{review.name}</strong>
                <img
                  src={`${path_to_avatar}/${review.name}`}
                  alt="avatar"
                  className="avatar rounded-circle"
                  height="30"
                  width="30"
                />
              </Toast.Header>
              <Toast.Body>
                <div className="content">
                  <p>Общее впечатление: {[...Array(5)].map((_, i) => (
                    <span key={i} className={review.overallRating > i ? 'filled-star' : 'empty-star'}>★</span>
                  ))}</p>
                  <p>Состояние корта: {[...Array(5)].map((_, i) => (
                    <span key={i} className={review.courtConditionRating > i ? 'filled-star' : 'empty-star'}>★</span>
                  ))}</p>
                  <p>Состояние колец: {[...Array(5)].map((_, i) => (
                    <span key={i} className={review.ringsConditionRating > i ? 'filled-star' : 'empty-star'}>★</span>
                  ))}</p>
                  <p>{review.comment}</p>
                </div>
              </Toast.Body>
            </Toast>
          ))
        ) : (
          <p>Нет отзывов для отображения.</p>
        )}
      </div>
      <Form onSubmit={submitReview}>
        <Form.Group controlId="formOverallRating">
          <Form.Label>Общее впечатление</Form.Label>
          <div className="wrapper mb-2">
            {[...Array(5)].map((_, i) => (
              <React.Fragment key={i}>
                <input
                  name="overallRating"
                  type="radio"
                  id={`overall-st${i + 1}`}
                  value={i + 1}
                  checked={overallRating === (i + 1)}
                  onChange={() => setOverallRating(i + 1)}
                  className="d-none" // Скрываем стандартный радио-элемент
                />
                <label htmlFor={`overall-st${i + 1}`} className="star-label" style={{cursor: 'pointer'}}>
                  <span className={overallRating >= (i + 1) ? 'filled-star' : 'empty-star'}>★</span>
                </label>
              </React.Fragment>
            ))}
          </div>
        </Form.Group>
        <Form.Group controlId="formCourtConditionRating">
          <Form.Label>Состояние корта</Form.Label>
          <div className="wrapper mb-2">
            {[...Array(5)].map((_, i) => (
              <React.Fragment key={i}>
                <input
                  name="courtConditionRating"
                  type="radio"
                  id={`court-st${i + 1}`}
                  value={i + 1}
                  checked={courtConditionRating === (i + 1)}
                  onChange={() => setCourtConditionRating(i + 1)}
                  className="d-none"
                />
                <label htmlFor={`court-st${i + 1}`} className="star-label" style={{cursor: 'pointer'}}>
                  <span className={courtConditionRating >= (i + 1) ? 'filled-star' : 'empty-star'}>★</span>
                </label>
              </React.Fragment>
            ))}
          </div>
        </Form.Group>
        <Form.Group controlId="formRingsConditionRating">
          <Form.Label>Состояние колец</Form.Label>
          <div className="wrapper mb-4">
            {[...Array(5)].map((_, i) => (
              <React.Fragment key={i}>
                <input
                  name="ringsConditionRating"
                  type="radio"
                  id={`rings-st${i + 1}`}
                  value={i + 1}
                  checked={ringsConditionRating === (i + 1)}
                  onChange={() => setRingsConditionRating(i + 1)}
                  className="d-none"
                />
                <label htmlFor={`rings-st${i + 1}`} className="star-label" style={{cursor: 'pointer'}}>
                  <span className={ringsConditionRating >= (i + 1) ? 'filled-star' : 'empty-star'}>★</span>
                </label>
              </React.Fragment>
            ))}
          </div>
        </Form.Group>
        <Form.Group controlId="formComment">
          <Form.Label>Комментарий</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            placeholder="Введите ваш отзыв"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            className="mb-3"
          />
        </Form.Group>
        <Button variant="primary" type="submit">Отправить отзыв</Button>
      </Form>
      <ToastContainer />
    </Container>
  );
}

export default Comment;
